#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Mon Jul  4 22:25:57 2022@author: thorknudsen"""import tkinter as tkimport numpy as npfrom tkmacosx import Buttonclass GUI:	def __init__(self, character):		self.modifier = 0		self.roll = 0		self.window = tk.Tk()		self.window.attributes('-fullscreen', True)		self.window.configure(bg="black")		self.addName(character.name)		self.addAbilityButtons(character.abilityModifiers)		#self.addRolledValue("hej")		#self.window.mainloop()	def addName(self, name):			self.label1 = tk.Label(self.window, text=name, font=('Times New Roman bold', 50), bg="black", fg="white")		self.label1.pack(padx=10, pady=20)		self.text2 = tk.StringVar()		self.text2.set("")		self.label2 = tk.Label(self.window, textvariable=self.text2, font=('Times New Roman bold', 60), bg="black", fg="white")		self.label2.pack(side = "bottom", pady=10)		self.text3 = tk.StringVar()		self.text3.set("Awaiting selection")		self.label3 = tk.Label(self.window, textvariable=self.text3, font=('Times New Roman bold', 60), bg="black", fg="white")		self.label3.pack(side = "bottom")	def addAbilityButtons(self, abilityModifiers):		buttonHeight = 60#3		buttonWidth = 160#15		xs = np.arange(0, 3) * 200		ys = np.arange(1, 7) * 100 + 50		colorScheme = ["#994236", "#495b4a", "#cd893b", "#3d5a80", "#adadad", "#98788b"]		font = ('Times New Roman bold', 22)		button1 = Button(self.window, text="Acrobatics", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[1], font=font, fg="white", command=lambda:self.abilityActivationFunction(0, abilityModifiers))		button1.place(x=xs[0], y=ys[0])		button2 = Button(self.window, text="Animal\nHandling", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[4], font=font, fg="white", command=lambda:self.abilityActivationFunction(1, abilityModifiers))		button2.place(x=xs[0], y=ys[1])		button3 = Button(self.window, text="Arcana", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[3], font=font, fg="white", command=lambda:self.abilityActivationFunction(2, abilityModifiers))		button3.place(x=xs[0], y=ys[2])		button4 = Button(self.window, text="Athletics", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[0], font=font, fg="white", command=lambda:self.abilityActivationFunction(3, abilityModifiers))		button4.place(x=xs[0], y=ys[3])		button5 = Button(self.window, text="Deception", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[5], font=font, fg="white", command=lambda:self.abilityActivationFunction(4, abilityModifiers))		button5.place(x=xs[0], y=ys[4])		button6 = Button(self.window, text="History", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[3], font=font, fg="white", command=lambda:self.abilityActivationFunction(5, abilityModifiers))		button6.place(x=xs[0], y=ys[5])		button7 = Button(self.window, text="Insight", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[4], font=font, fg="white", command=lambda:self.abilityActivationFunction(6, abilityModifiers))		button7.place(x=xs[1], y=ys[0])		button8 = Button(self.window, text="Intimidation", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[5], font=font, fg="white", command=lambda:self.abilityActivationFunction(7, abilityModifiers))		button8.place(x=xs[1], y=ys[1])		button9 = Button(self.window, text="Investigation", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[3], font=font, fg="white", command=lambda:self.abilityActivationFunction(8, abilityModifiers))		button9.place(x=xs[1], y=ys[2])		button10 = Button(self.window, text="Medicine", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[4], font=font, fg="white", command=lambda:self.abilityActivationFunction(9, abilityModifiers))		button10.place(x=xs[1], y=ys[3])		button11 = Button(self.window, text="Nature", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[3], font=font, fg="white", command=lambda:self.abilityActivationFunction(10, abilityModifiers))		button11.place(x=xs[1], y=ys[4])		button12 = Button(self.window, text="Perception", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[4], font=font, fg="white", command=lambda:self.abilityActivationFunction(11, abilityModifiers))		button12.place(x=xs[1], y=ys[5])		button13 = Button(self.window, text="Performance", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[5], font=font, fg="white", command=lambda:self.abilityActivationFunction(12, abilityModifiers))		button13.place(x=xs[2], y=ys[0])		button14 = Button(self.window, text="Persuasion", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[5], font=font, fg="white", command=lambda:self.abilityActivationFunction(13, abilityModifiers))		button14.place(x=xs[2], y=ys[1])		button15 = Button(self.window, text="Religion", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[3], font=font, fg="white", command=lambda:self.abilityActivationFunction(14, abilityModifiers))		button15.place(x=xs[2], y=ys[2])		button16 = Button(self.window, text="Sleight\nof Hand", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[1], font=font, fg="white", command=lambda:self.abilityActivationFunction(15, abilityModifiers))		button16.place(x=xs[2], y=ys[3])		button17 = Button(self.window, text="Stealth", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[1], font=font, fg="white", command=lambda:self.abilityActivationFunction(16, abilityModifiers))		button17.place(x=xs[2], y=ys[4])		button18 = Button(self.window, text="Survival", height=buttonHeight, width=buttonWidth, highlightbackground='#000000', bg=colorScheme[5], font=font, fg="white", command=lambda:self.abilityActivationFunction(17, abilityModifiers))		button18.place(x=xs[2], y=ys[5])	def addRolledValue(self, roll):		#label1 = tk.Label(self.window, text="{} + {} = {}".format(roll, 0, 0), font=('Times New Roman bold', 60), bg="black", fg="white")		#label1.pack(side = "bottom", pady=10)		#label2 = tk.Label(self.window, text=roll, font=('Times New Roman bold', 60), bg="black", fg="white")		#label2.pack(side = "bottom")		pass			def abilityActivationFunction(self, abilityIdx, abilityModifiers):		#self.isAbilityActive = True		self.modifier = abilityModifiers[abilityIdx]		self.changeText2()		self.changeText3(abilityIdx)	def changeText2(self):		if self.modifier >= 0:			self.text2.set("{} + {} = {}".format(self.roll, self.modifier, self.roll + self.modifier))		else: 			self.text2.set("{} - {} = {}".format(self.roll, abs(self.modifier), self.roll + self.modifier))	#def changeTest(self, roll):		#self.text2.set("{}".format(roll))	def changeText3(self, idx):		abilities = ["Acrobatics", "Animal Handling", "Arcana", "Athletics", "Deception", "History", 					 "Insight", "Intimidation", "Investigation", "Medicine", "Nature", "Perception", 					 "Performance", "Persuasion", "Religion", "Sleight of Hand", "Stealth", "Survival"]		self.text3.set(abilities[idx])	def setRoll(self, roll):		self.roll = roll		self.changeText2()