#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Mon Jul  4 22:25:57 2022@author: thorknudsen"""from character import Characterfrom serialCommunication import SerialCommunicationfrom gui import GUIimport timecharacterId = 65349572port = "/dev/cu.usbmodem14301"baudRate = 9600isDataGood = Falseif __name__ == "__main__":        character = Character(characterId)        serialCommunication = SerialCommunication(port, baudRate)    gui = GUI(character, serialCommunication)    while serialCommunication.isConnected:        serialCommunication.readData(gui)    gui.window.mainloop()